import streamlit as st

st.title("Containers")
container = st.container(border=True,
                         height=250)

with container:
    st.write("THIS IS IN A CONTAINER")

    file_path = "data:image/png;base64,"
    st.image(file_path,
             caption="THIS IS MY HAPPY FACE",
             width=150)

st.write("THIS IS NOT IN THE CONTAINER")
st.write("MORE CONTAINERS")

row1 = st.columns(3)
row2 = st.columns(3)
# you can use this code to create tiles
for col in row1 + row2:
    tile = col.container(height=100)
    tile.title(":balloon:")