import streamlit as st

st.title("IMAGES")

# a bit of an overkill but you can create a select box that will align the image
vertical_alignment = st.selectbox("What alignment would you like",
             ['top', 'bottom', 'center']
             )

# creating two columns
c1, c2 = st.columns(2, vertical_alignment=vertical_alignment)

# place image on first columns
with c1:
    st.header("IMAGE 1")
    file_path = "data:image/png;base64,"
    st.image(file_path,
             caption="THIS IS MY HAPPY FACE",
             use_column_width="always")
# place second image on second column
with c2:
    st.header("IMAGE 2")
    file_path2 = "data:image/jpeg;base64,"
    st.image(file_path2,
             caption="THIS IS MY SAD FACE",
             use_column_width="always")